import React,{useState,useEffect} from "react";
import '../styles/ListRestaurants.css';

const ListRestaurant = ({children}) =>{
    const [height, setHeight] = useState(640); // Початкова висота 200px (може бути іншою за замовчуванням)

    useEffect(() => {
      // Приклад: зміна висоти при завантаженні сторінки
      if (window.location.pathname === '/TopRated') {
        setHeight(440); // Змінюємо висоту на сторінці '/your-specific-page' на 300px
      }
    }, []);
    return(
        <div className="listRestourants" style={{ height: '${height}px' }}>
            {children}
        </div>
    )
}

export default ListRestaurant;
